import React, { useState, useRef, useEffect } from "react";
import { Dialog } from "primereact/dialog";
import { InputText } from "primereact/inputtext";
import { Button } from "primereact/button";
// import { Toast } from 'primereact/toast';


const CreateGroupDialog = ({ visible, onHide }) => {
  const [groupName, setGroupName] = useState("");
  const [searchedUser, setSearchedUser] = useState("");
  const [members, setMembers] = useState([]);
  const toast = useRef(null);

  const showError = () => {
      toast.current.show({severity:'error', summary: 'Error', detail:'Username Is Invalid', life: 3000});
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Submit form data to the backend using fetch API
    try {
      const response = await fetch("/submit_form", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({ groupName, searchedUser }),
      });
      const data = await response.json();
      console.log(data); // Handle response from the backend as needed
    } catch (error) {
      console.error("Error submitting form:", error);
    }
  };

  useEffect(() => {
    const fetchSearchResults = async () => {
      if (!searchedUser) {
        return; // Early return if username is empty
      }

      try {
        const response = await fetch("http://127.0.0.1:5000/search_user", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ username: searchedUser }),
        });
        const data = await response.json();
        if (data.success) {
          setMembers([...members, searchedUser]);
        } else {
          showError();
        }
      } catch (error) {
        console.error("Error searching user:", error);
        showError("An error occurred while searching users.");
      }
    };

    fetchSearchResults();
  }, [searchedUser, showError]); 


  return (
    <Dialog
      header="Create Group"
      visible={visible}
      style={{ backgroundColor: "var(--primary-color)", width: "50vw" }}
      onHide={onHide}
    >
      <form onSubmit={handleSubmit} className="p-fluid">
        <div className="p-inputgroup flex-1">
          <span className="p-inputgroup-addon">
            <i className="pi pi-users"></i>
          </span>
          <InputText
            placeholder="Group Name"
            value={groupName}
            onChange={(e) => setGroupName(e.target.value)}
          />
        </div>
        <br />

        <label htmlFor="username">Add Members:</label>
        <div className="p-inputgroup flex-1">
          <InputText
            placeholder="Enter Username"
            value={searchedUser}
            onChange={(e) => setSearchedUser(e.target.value)}
          />
          <Button
            icon="pi pi-search"
            className="p-button"
            disabled
          />
        </div>
        <br />
        {members.length > 0 && (
          <div>
            <p>Members added:</p>
            <ul>
              {members.map((member, index) => (
                <li key={index}>{member}</li>
              ))}
            </ul>
          </div>
        )}
        <Button label="Submit" type="submit" icon="pi pi-check mt-2" />
      </form>
    </Dialog>
  );
};

export default CreateGroupDialog;
