import React, { useEffect, useState, useRef } from "react";
import { useSelector } from "react-redux";
import Message from "./Message";
import { GoogleGenerativeAI } from "@google/generative-ai";

const BotMessages = () => {
  const groupID = useSelector((state) => state.groupID);
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);


  const [chatHistory, setChatHistory] = useState([]);
  const [generatedResponse, setGeneratedResponse] = useState('');

  useEffect(() => {
    const run = async () => {
      const API_KEY = "AIzaSyB0dmVIWZ5raZtTD230bCO2Da-j-PW2s8o";
      const genAI = new GoogleGenerativeAI(API_KEY);
      
      const model = genAI.getGenerativeModel({ model: "gemini-pro" });

      const chat = model.startChat({
        history: chatHistory,
        generationConfig: {
          maxOutputTokens: 100,
        },
      });

      const msg = "How many paws are in my house?";

      const result = await chat.sendMessage(msg);
      const response = await result.response;
      const text = await response.text();
      
      setGeneratedResponse(text);
      setChatHistory([
        ...chatHistory,
        {
          role: "user",
          parts: [{ text: msg }],
        },
        {
          role: "model",
          parts: [{ text }],
        }
      ]);
    }
    console.log(chatHistory);

    run();
  }, [chatHistory]);

  useEffect(() => {
    // Scroll to bottom after a small delay when messages are loaded
    const timer = setTimeout(() => {
      if (messagesEndRef.current) {
        messagesEndRef.current.scrollIntoView({
          behavior: "smooth",
          block: "end",
        });
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [messages]);

  return (
    <div className="messages">
      {messages.length === 0 ? (
        <div className="no-messages">Start the conversation!</div>
      ) : (
        messages.map((message, index) => (
          <Message key={index} message={message} />
        ))
      )}
      <div ref={messagesEndRef} />
    </div>
  );
};

export default BotMessages;
