import React, { useEffect } from "react";
import Message from "./Message";
import { useSelector } from "react-redux";

const Messages = () => {
  const groupID = useSelector((state) => state.groupID);
  //   const [messages, setMessages] = useState([]);
  //   const { data } = useContext(ChatContext);

  useEffect(() => {
    const fetchMessages = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/get_messages", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            groupID: groupID,
          }),
        });
        const data = await response.json();
        if (data.success)
        console.log(data); // Handle response from the backend as needed
      else
        console.error("ERROR:",data.message); // Log response from the backend
      } catch (error) {
        console.error(error);
      }
    };

    fetchMessages();
  }, []);

  return (
    <div className="messages">
      {/* {messages.map((m) => ( */}
      <Message />
      {/* ))} */}
    </div>
  );
};

export default Messages;
