import React, { useEffect, useState } from "react";

const Chats = () => {
  const [userData, setUserData] = useState(null);

  const handleSelect = () => {
    localStorage.removeItem('sessionID'); // Remove session ID from storage
    window.location.href = '/'; // Redirect to homepage
  };

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const sessionID = localStorage.getItem('sessionID');
        const response = await fetch("http://127.0.0.1:5000/show_group", {
          method: "GET",
          headers: {
            Authorization: `Bearer ${sessionID}`, // Send session ID
            "Content-Type": "application/json",
          },
        });
        const data = await response.json();
        if (data.success) {
          setUserData(data); // Set user data
        } else {
          console.error(data.message); // Handle error
        }
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, []);

  return (
    <div className="chats">
      {userData && userData.groups.map(group => (
        <div
          className="userChat"
          key={group.group_ID}
          // onClick={() => handleSelect(key)}
        >
          <i className="pi pi-users" style={{ fontSize: '2.0rem' }}> </i>
          <div className="userChatInfo">
            <span>{group.group_name}</span>
            <p>hello!</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Chats;
