import React, { useEffect, useState } from 'react';
import { Button } from 'primereact/button';
import CreateGroupDialog from '../components/CreateGroupDialog';        
        

const Dashboard = () => {
  const [userData, setUserData] = useState(null);
  const [createGroupVisible, setCreateGroupVisible] = useState(false);

  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const sessionID = localStorage.getItem('sessionID');
        const response = await fetch('http://127.0.0.1:5000/dashboard', {
          method: 'GET',
          headers: {
            'Authorization': `Bearer ${sessionID}`, // Send session ID
            'Content-Type': 'application/json'
          }
        });
        const data = await response.json();
        setUserData(data); // Set user data
      } catch (error) {
        console.error(error);
      }
    };

    fetchUserData();
  }, []);

  const handleLogout = () => {
    localStorage.removeItem('sessionID'); // Remove session ID from storage
    window.location.href = '/'; // Redirect to homepage
  };

  return (
    <div>
      <h1>Dashboard</h1>
      {userData && (
        <div>
          <p>Welcome, {userData.username}</p>
          {/* Display other user data */}
        </div>
      )}
      <button onClick={handleLogout}>Logout</button>
      <br /><br /><br />
      <Button label="Create Group" icon="pi pi-external-link" onClick={() => setCreateGroupVisible(true)} />
      <CreateGroupDialog visible={createGroupVisible} onHide={() => setCreateGroupVisible(false)} />
    </div>
  );
};

export default Dashboard;
