import React, { useState, useRef } from "react";
import { Link } from "react-router-dom";
import { Toast } from 'primereact/toast';
import "./Register.css"; // Import your CSS file

const Register = () => {
  const toast = useRef(null);
  const [file, setFile] = useState(null);
  const inputRef = useRef(null);
  const [formsData, setFormData] = useState({
    username: "",
    email: "",
    password: "",
  });

  const showError = (msg) => {
    toast.current.show({ severity: 'error', summary: 'Error', detail: msg, life: 3000 });
  };

  const showSuccess = (msg) => {
    toast.current.show({ severity: 'success', summary: 'Success', detail: msg, life: 3000 });
  };

  const handleChange = (e) => {
    setFormData({ ...formsData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setFile(e.target.files[0]);
  };

  const handleFileDelete = () => {
    setFile(null);
    inputRef.current.value = null;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    // Add form validation logic here
    if (!formsData.username || !formsData.email || !formsData.password) {
      showError("All fields are required.");
      return;
    }
    // Your submit logic
    showSuccess("Registration successful!");
  };

  return (
    <div classname="reg_register-container">
      <Toast ref={toast} />
      <h1 classname="reg_register-header">Register</h1>
      <form classname="reg_register-form" onSubmit={handleSubmit}>
        <input
          type="text"
          name="username"
          placeholder="Username"
          classname="reg_input-field"
          onChange={handleChange}
        />
        <input
          type="email"
          name="email"
          placeholder="Email"
          classname="reg_input-field"
          onChange={handleChange}
        />
        <input
          type="password"
          name="password"
          placeholder="Password"
          classname="reg_input-field"
          onChange={handleChange}
        />
        <label htmlFor="file" classname="reg_add-avatar-label">
          <i classname="reg_pi pi-user add-avatar-icon"></i>
          <span>Add an avatar</span>
          <input
            id="file"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            ref={inputRef}
            classname="reg_file-input"
          />
        </label>
        {file && (
          <div classname="reg_file-info-container">
            <span classname="reg_file-info">{file.name}</span>
            <i
              classname="reg_pi pi-times-circle delete-file-icon"
              onClick={handleFileDelete}
            ></i>
          </div>
        )}
        <button type="submit" classname="reg_submit-button">Register</button>
      </form>
      <Link to="/login" classname="reg_login-link">Already Have An Account? Log In</Link>
    </div>
  );
};

