import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom"; // Import useParams
// import Cam from "../img/cam.png";
// import Add from "../img/add.png";
// import More from "../img/more.png";
// import Messages from "./Messages";
import Input from "./Input";
// import { ChatContext } from "../context/ChatContext";

const Chat = () => {
  const { param: groupID } = useParams(); // Access the 'param' URL parameter
  const [groupName, setGroupName] = useState("");

  useEffect(() => {
    const fetchGroupChat = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/group_param", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ group_ID: groupID }),
        });
        const data = await response.json();
        console.log(data); // Check response data
        if (data.success) {
          setGroupName(data.name); // Set group name
        } else {
          console.error(data.message); // Handle error
        }
      } catch (error) {
        console.error(error);
      }
    };

    if (groupID) {
      fetchGroupChat();
    }
  }, [groupID]); // Re-run useEffect when groupID changes

  return (
    <div className="chat">
      <div className="chatInfo">
        {groupName && <span>{groupName}</span>} {/* Render only if groupName exists */}
        <div className="chatIcons">
          {/* <img src={Cam} alt="" />
          <img src={Add} alt="" />
          <img src={More} alt="" /> */}
        </div>
      </div>
      {/* <Messages /> */}
      <Input />
    </div>
  );
};

export default Chat;
