import React, { useState } from "react";
import { Button } from "primereact/button";
import { useSelector } from 'react-redux';

const Input = () => {
  const [text, setText] = useState("");
  const groupID = useSelector((state) => state.groupID);

  const handleSend = async () => {
    const timestamp = new Date().toISOString(); // Get current timestamp in ISO format
    const senderID = localStorage.getItem('sessionID'); // sender ID

    if (!text.trim()) {
      alert("Please enter a message");
      return;
    }
    
    try {
      const response = await fetch("http://127.0.0.1:5000/send_message", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text: text,
          timestamp: timestamp,
          senderID: senderID,
          groupID: groupID,
        }),
      });
      const data = await response.json();
      console.log(data); // Log response from the backend
      setText(""); // Clear input field after sending
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="input">
      <input
        type="text"
        placeholder="Type something..."
        onChange={(e) => setText(e.target.value)}
        value={text}
      />
      <div className="send">
        <label htmlFor="file">
            <i className="pi pi-image" style={{ fontSize: '1.5rem', color: '#708090' }}></i>
        </label>
        <Button onClick={handleSend} label="Send" icon="pi pi-send" />
      </div>
    </div>
  );
};

export default Input;
