import React, { useEffect, useState, useRef } from "react";
import BotMessage from "./BotMessage";
import { GoogleGenerativeAI } from "@google/generative-ai";
import { Button } from "primereact/button";

const EcoBotChat = () => {
  const messagesEndRef = useRef(null);
  const [chatHistory, setChatHistory] = useState([]);
  const [inputText, setInputText] = useState("");
  const [loading, setLoading] = useState(false);


  // API key setup
  const API_KEY = "AIzaSyB0dmVIWZ5raZtTD230bCO2Da-j-PW2s8o";
  const genAI = new GoogleGenerativeAI(API_KEY);

  // For text-only input, use the gemini-pro model
  const model = genAI.getGenerativeModel({ model: "gemini-pro" });

  const handleSend = async () => {
    // Construct chat history
    const newChatHistory = [...chatHistory, { role: "user", parts: [{ text: inputText }] }];

    setChatHistory(newChatHistory);
    setLoading(true);

    const chat = model.startChat({
      history: newChatHistory,
      generationConfig: {
        maxOutputTokens: 100,
      },
    });

    const result = await chat.sendMessage(inputText);
    const response = await result.response;
    const text = await response.text();
    const history = await chat.getHistory();
    setChatHistory(history);
    console.log("HISTORY", chatHistory);
    console.log("TEXT", text);
    setLoading(false);
  };
  return (
    <div className="chat">
      <div className="chatInfo">
        <span>EcoBot</span>
        <div className="chatIcons">
          {/* <img src={Cam} alt="" />
          <img src={Add} alt="" />
          <img src={More} alt="" /> */}
        </div>
      </div>

      <div className="messages">
        {chatHistory.length === 0 ? (
          <div className="no-messages">Start the conversation!</div>
        ) : (
          chatHistory.map((message, index) => (
            <BotMessage key={index} message={message} />
          ))
        )}
        <div ref={messagesEndRef} />
      </div>

      <div className="input">
        <input
          type="text"
          placeholder="Type something..."
          onChange={(e) => setInputText(e.target.value)}
          value={inputText}
        />
        <div className="send">
          <Button onClick={handleSend} label="Send" icon="pi pi-send" />
        </div>
      </div>
    </div>
  );
};

export default EcoBotChat;
