import React, { useEffect, useState } from "react";
// import Cam from "../img/cam.png";
// import Add from "../img/add.png";
// import More from "../img/more.png";
// import Messages from "./Messages";
import Input from "./Input";
// import { ChatContext } from "../context/ChatContext";
import { useLocation } from "react-router-dom";

const Chat = () => {
  const [groupName, setGroupName] = useState("");
  const [groupID, setGroupID] = useState("");

  const location = useLocation();
  const urlParams = new URLSearchParams(location.search);
  const groupIDParam = urlParams.get("param");

  useEffect(() => {
    // const urlParams = new URLSearchParams(window.location.search);
    // const groupIDParam = urlParams.get("param");

    const fetchGroupChat = async () => {
      try {
        const response = await fetch("http://127.0.0.1:5000/group_param", {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({ group_ID: groupIDParam }),
        });
        const data = await response.json();
        console.log(data); // Check response data
        if (data.success) {
          setGroupName(data.name); // Set group name
        } else {
          console.error(data.message); // Handle error
        }
      } catch (error) {
        console.error(error);
      }
    };

    if (groupIDParam) {
      setGroupID(groupIDParam);
    }
    
    fetchGroupChat(); // Fetch group chat whenever groupIDParam changes
  }, [groupID]);

  return (
    <div className="chat">
      <div className="chatInfo">
        {groupName && <span>{groupName}</span>} {/* Render only if groupName exists */}
        <div className="chatIcons">
          {/* <img src={Cam} alt="" />
          <img src={Add} alt="" />
          <img src={More} alt="" /> */}
        </div>
      </div>
      {/* <Messages /> */}
      <Input />
    </div>
  );
};

export default Chat;
