import { Button } from "primereact/button";
import {useSelector } from 'react-redux';


const Input = () => {
  const [text, setText] = useState("");
  
  const handleSend = () => {
    const timestamp =  new Date().toLocaleString(); // Get current timestamp
    const senderID = localStorage.getItem('sessionID'); // sender ID
    const groupID = useSelector((state) => state.groupID);

    console.log("Message:",text)
    console.log("Timestamp:", timestamp)
    console.log("Sender ID:", senderID)
    setText("");
    
  };

  return (
    <div className="input">
      <input
        type="text"
        placeholder="Type something..."
        onChange={(e) => setText(e.target.value)}
        value={text}
      />
      <div className="send">
        {/* <img src={Attach} alt="" /> */}
        {/* <input
          type="file"
          style={{ display: "none" }}
          id="file"
          onChange={(e) => setImg(e.target.files[0])}
        /> */}
        <label htmlFor="file">
            <i className="pi pi-image" style={{ fontSize: '1.5rem', color: '#708090' }}></i>
        </label>
        <Button onClick={handleSend} label="Send" icon="pi pi-send"  />
      </div>
    </div>
  );
};

export default Input;
