import React, { useState, useRef } from 'react';
import {Link } from 'react-router-dom';


const Register = () => {
  const [file, setFile] = useState(null);
  const inputRef = useRef(null);
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    password: '',
  });

  const handleChange = e => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    console.log(e.target.files[0]);

    setFile(e.target.files[0]);
  };

  const handleFileDelete = () => {
    setFile(null);
    inputRef.current.value = null;
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const formData = new FormData();
      formData.append('username', formData.username);
      formData.append('email', formData.email);
      formData.append('password', formData.password);
      formData.append('profile_image', file);
      console.log("FormData", formData); // Log FormData to check if profile image is included

      
      const response = await fetch('http://127.0.0.1:5000/register', {
        method: 'POST',
        body: formData,
      });

      const data = await response.json();
      if (response.ok) {
        window.location.href = '/login';
      } else {
        console.error(data.message); // Handle registration error
      }
    } catch (error) {
      console.error(error); // Handle network error
      console.log(formData); // Handle network error
    }
  };

  return (
    <div>
      <h1>Register</h1>
      <form onSubmit={handleSubmit}>
        <input type="text" name="username" placeholder="Username" onChange={handleChange} />
        <input type="email" name="email" placeholder="Email" onChange={handleChange} />
        <input type="password" name="password" placeholder="Password" onChange={handleChange} />
        <label htmlFor="file">
          <i className="pi pi-user" style={{ fontSize: '1.5rem', color: '#708090', cursor: 'pointer' }}></i>
          <span>Add an avatar</span>
          <input
            id="file"
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            ref={inputRef}
            style={{ display: 'none' }}
          />
        </label>
        {file && (
            <div className="file-info" style={{display: 'contents', backgroundColor: '#708090'}}>
              <span>{file.name}</span>
              <i onClick={handleFileDelete} className="pi pi-times-circle"></i>
            </div>
        )}
        <button type="submit">Register</button>
      </form>
      <Link to="/login">Already Have An Account? Log In</Link>
    </div>
  );
};

export default Register;
