import React,{ useEffect, useState} from 'react';
import {useSelector } from 'react-redux';
import Input from './Input';
import Messages from './Messages';

const Chat = () => {
  const groupName = useSelector((state) => state.groupName);
  const [bot, setBot] = useState('false');

  useEffect(() => { 
    if (groupName == 'EcoBot'){
      setBot('true');
      console.log('BOT','yes');
    } 
    else
      console.log('BOT','no');
  }, [groupName]);
  
  return (
    <div className="chat">
      <div className="chatInfo">
        {groupName && <span>{groupName}</span>} {/* Render only if groupName exists */}
        <div className="chatIcons">
          {/* <img src={Cam} alt="" />
          <img src={Add} alt="" />
          <img src={More} alt="" /> */}
        </div>
      </div>
      <Messages />
      <Input />
    </div>
  );
};

export default Chat;
