import React, { useState } from "react";
import { Button } from "primereact/button";
import { useSelector } from 'react-redux';

const Input = () => {
  const [text, setText] = useState("");
  const groupID = useSelector((state) => state.groupID);

  function formatDate(timestamp) {
    const date = new Date(timestamp);
    const year = date.getFullYear();
    const month = (date.getMonth() + 1).toString().padStart(2, '0');
    const day = date.getDate().toString().padStart(2, '0');
    const hours = date.getHours().toString().padStart(2, '0');
    const minutes = date.getMinutes().toString().padStart(2, '0');
    const seconds = date.getSeconds().toString().padStart(2, '0');
    return `${year}-${month}-${day} ${hours}:${minutes}:${seconds}`;
  }

  const handleSend = async () => {
    const timestamp = formatDate(new Date().toLocaleString('en-US', { timeZone: 'Asia/Kolkata' }).replace(/,/g, ''));
    const senderID = localStorage.getItem('sessionID'); // sender ID
    
    if (!text.trim()) {
      alert("Please enter a message");
      return;
    }

    try {
      const response = await fetch('http://127.0.0.1:5000/send_text', {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          text: text,
          timestamp: timestamp,
          senderID: senderID,
          groupID: groupID,
        }),
      });
      const data = await response.json();
      if (data.success){
        console.log(data); // Handle response from the backend as needed
        setText(""); // Clear input field after sending
      }else
        console.error("ERROR:",data.message); // Log response from the backend
    } catch (error) {
      console.error(error);
    }
  };

  return (
    <div className="input">
      <input
        type="text"
        placeholder="Type something..."
        onChange={(e) => setText(e.target.value)}
        value={text}
      />
      <div className="send">
        <input
          type="file"
          accept="image/*"
          // onChange={handleImageChange}
        />
        <label htmlFor="file">
            <i className="pi pi-image" style={{ fontSize: '1.5rem', color: '#708090' }}></i>
        </label>
        <Button onClick={handleSend} label="Send" icon="pi pi-send" />
      </div>
    </div>
  );
};

export default Input;
