import React, { useEffect, useState, useRef } from "react";
import { useSelector } from "react-redux";
import Message from "./Message";
import {  GenerativeModel } from "google-generativeai";

const BotMessages = () => {
  const groupID = useSelector((state) => state.groupID);
  const [messages, setMessages] = useState([]);
  const messagesEndRef = useRef(null);


  useEffect(() => {
    const runChat = async () => {
      // Replace 'YOUR_API_KEY' with your actual API key
      GenerativeModel.configure({ api_key: "YOUR_API_KEY" });

      // Set up the model
      const generationConfig = {
        "temperature": 1,
        "top_p": 0.95,
        "top_k": 0,
        "max_output_tokens": 8192,
      };
      
      const safetySettings = [
        {
          "category": "HARM_CATEGORY_HARASSMENT",
          "threshold": "BLOCK_MEDIUM_AND_ABOVE"
        },
        {
          "category": "HARM_CATEGORY_HATE_SPEECH",
          "threshold": "BLOCK_MEDIUM_AND_ABOVE"
        },
        {
          "category": "HARM_CATEGORY_SEXUALLY_EXPLICIT",
          "threshold": "BLOCK_MEDIUM_AND_ABOVE"
        },
        {
          "category": "HARM_CATEGORY_DANGEROUS_CONTENT",
          "threshold": "BLOCK_MEDIUM_AND_ABOVE"
        },
      ];

      const model = new GenerativeModel({
        model_name: "gemini-1.5-pro-latest",
        generation_config: generationConfig,
        safety_settings: safetySettings
      });

      const convo = await model.start_chat({ history: [] });

      await convo.send_message("YOUR_USER_INPUT");
      console.log(convo.last.text);
    }

    runChat();
  }, []);

  useEffect(() => {
    // Scroll to bottom after a small delay when messages are loaded
    const timer = setTimeout(() => {
      if (messagesEndRef.current) {
        messagesEndRef.current.scrollIntoView({
          behavior: "smooth",
          block: "end",
        });
      }
    }, 100);

    return () => clearTimeout(timer);
  }, [messages]);

  return (
    <div className="messages">
      {messages.length === 0 ? (
        <div className="no-messages">Start the conversation!</div>
      ) : (
        messages.map((message, index) => (
          <Message key={index} message={message} />
        ))
      )}
      <div ref={messagesEndRef} />
    </div>
  );
};

export default BotMessages;
