import React, { useState } from "react";
// import Img from "../img/img.png";
// import Attach from "../img/attach.png";
// import { AuthContext } from "../context/AuthContext";
// import { ChatContext } from "../context/ChatContext";
// import {
//   arrayUnion,
//   doc,
//   serverTimestamp,
//   Timestamp,
//   updateDoc,
// } from "firebase/firestore";
// import { db, storage } from "../firebase";
// import { v4 as uuid } from "uuid";
import { Button } from "primereact/button";
import {useSelector } from 'react-redux';


const Input = () => {
  const [text, setText] = useState("");
  const groupID = useSelector((state) => state.groupID);
  
  const handleSend = () => {
    const timestamp =  new Date().toLocaleString(); // Get current timestamp
    const senderID = localStorage.getItem('sessionID'); // sender ID

    console.log("Message:",text)
    console.log("Timestamp:", timestamp)
    console.log("Sender ID:", senderID)
    console.log("Group ID:", groupID)
    setText("");
    
  };

  return (
    <div className="input">
      <input
        type="text"
        placeholder="Type something..."
        onChange={(e) => setText(e.target.value)}
        value={text}
      />
      <div className="send">
        {/* <img src={Attach} alt="" /> */}
        {/* <input
          type="file"
          style={{ display: "none" }}
          id="file"
          onChange={(e) => setImg(e.target.files[0])}
        /> */}
        <label htmlFor="file">
            <i className="pi pi-image" style={{ fontSize: '1.5rem', color: '#708090' }}></i>
        </label>
        <Button onClick={handleSend} label="Send" icon="pi pi-send"  />
      </div>
    </div>
  );
};

export default Input;
